$(document).ready(function(){$("#btn-enviar-noticia").click(function(c){c.preventDefault();var b=$("#titulo-crear").val();var a=$("#texto-crear").val();
$.ajax({url:"crear-noticia.php",type:"POST",dataType:"JSON",data:{titulo:b,texto:a},success:function(d){window.location.replace("http://"+d.enlace+"/welcome.php");
}});});$(".editar-noticia").click(function(c){c.preventDefault();var d=$(this).val();var b=$("#titulo-editar-"+d).val();var a=$("#texto-editar-"+d).val();
$.ajax({url:"editar-noticia.php",type:"POST",dataType:"json",data:{id:d,titulo:b,texto:a},success:function(e){window.location.replace("http://"+e.enlace+"/welcome.php");
}});});$(".eliminar-noticia").click(function(a){a.preventDefault();var b=$(this).val();$.ajax({url:"eliminar-noticia.php",type:"POST",dataType:"json",data:{id:b},success:function(c){window.location.replace("http://"+c.enlace+"/welcome.php");
}});});});